mapper.select:
SELECT u2.type from u_user_role u1 LEFT JOIN u_role u2 on
u1.rid = u2.id
WHERE u1.uid = #{userId}

select * from u_permission

	where name like concat("%",#{findContent},"%")

limit ${pageNo},${pageSize}

select * from u_role
where ( name like concat("%",#{findContent},"%") or type like concat("%",#{findContent},"%") )
limit ${pageNo},${pageSize}

select ur.id,ur.type,ur.name,group_concat(up.name) permissionNames,group_concat(up.id)permissionIds from u_role ur left join u_role_permission urp on urp.rid = ur.id left join u_permission up on up.id = urp.pid
and ( LOWER(ur.name) like CONCAT("%",#{findContent,jdbcType=VARCHAR},"%") or LOWER(ur.type) like CONCAT("%",#{findContent,jdbcType=VARCHAR},"%") )
group by ur.id limit #{pageNo},#{pageSize}
